<template>    <section :class="[panelClass,cname]">        <h4>-{{title}}-</h4>        <slot></slot>    </section></template><script>    export default {        name: "panel",        props: {            title: {                type: String,                default: ''            },            cname: {                type: String,                default: ''            }        },        data() {            return {                panelClass: 'panel'            }        }    }</script><style scoped lang="scss">    @import "../../css/element";    .panel {        @include panel();    }    .panel.novice{        >h4{            border-bottom: 1px solid #ddd;        }    }</style>