<template>    <section :class="cname">        <swiper :options="swiperOption" :not-next-tick="swiperOption.notNextTick">            <swiper-slide v-for="item in items" :key="item.href">                <router-link :to="{name: item.href}">                    <img :src="item.src">                </router-link>            </swiper-slide>            <div class="swiper-pagination" v-if="swiperOption.pagination" slot="pagination"></div>        </swiper>    </section></template><script>    import {swiper, swiperSlide} from 'vue-awesome-swiper'    export default {        name: "slider",        props: {          items: {              type: Array,              default () {                  return []              }          },          cname: {              type: String,              default: ''          },          swiperOption: {              type: Object,              default () {                  return {                      autoplay: {                          disableOnInteraction: false                      },                      loop: true,                      pagination: {                          el: '.swiper-pagination',                          clickable: true                      },                      notNextTick: false                  }              }          }        },        data () {            return {}        },        components: {            swiper,            swiperSlide        }    }</script><style scoped lang="scss">@import "~swiper/dist/css/swiper.css";.slider{    margin-top: 120px;    img{        width: 100%;    }}</style>